  const {user } = ChatState();
  return (
    <div style={{width : "100%"}}>
      {user && <SideDrawer></SideDrawer>}
      <Box>
        {/* {user && <MyChats></MyChats>} */}
        {/* {user && <Chatbox></Chatbox>} */}
      </Box>
      
    </div>
  )
}

export default ChatPage
