import { ArrowBackIcon } from '@chakra-ui/icons';
import { Box, FormControl, IconButton, Input, Spinner, Text, useToast } from '@chakra-ui/react';
import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { getSender,getSenderFull } from '../config/chatLogics';
import { ChatState } from '../Context/ChatProvider'
import ProfileModal from './miscellanious/ProfileModal';
import UpdateGroupChatModal from "./miscellanious/updateGroupChatModal";

const SingleChats = ({setFetchAgain,fetchAgain}) => {
  const [messages, setMessages] = useState([]);
  const [loading, setLoading] = useState(false);
  const [newMessage, setNewMessage] = useState("");
  const {user,selectedChat,setSelectedChat} = ChatState();
  const toast = useToast();

  const sendMessage = async(event) =>{
    if (event.key === "Enter" && newMessage){
      try {
        const config = {
          headers: {
            "Content-type": "application/json",
            Authorization: `Bearer ${user.token}`,
          },
          
        };
        
        setNewMessage("");
        const { data } = await axios.post("/api/message",
        {
          content: newMessage,
          chatId: selectedChat._id,
        },
        config
      );
      console.log(data);
      setMessages([...messages, data]);
    }catch(error){
      toast({
        title: "Error Occured!",
        description: `sorry , ${user.name} Failed to send the Message`,
        status: "error",
        duration: 5000,
        isClosable: true,
        position: "bottom",
      });
    }

  }}

  const typingHandler = (e) =>{
    setNewMessage(e.target.value);


  }

  const fetchMessages = async () => {
    if (!selectedChat) return;

    try {
      const config = {
        headers: {
          Authorization: `Bearer ${user.token}`,
        },
      };

      setLoading(true);

      const { data } = await axios.get(
        `/api/message/${selectedChat._id}`,
        config
      );
      console.log(messages);
      setMessages(data);
      setLoading(false);

    
    } catch (error) {
      toast({
        title: "Error Occured!",
        description: `sorry , ${user.name} Failed to load the Message`,
        status: "error",
        duration: 5000,
        isClosable: true,
        position: "bottom",
      });
    }


  }

  
  
 

  useEffect(() => {
    fetchMessages();

    // eslint-disable-next-line
  }, [selectedChat]);
  return (
    <>
      {selectedChat?
      (<>
      <Text
        fontSize={{ base: "28px", md: "30px" }}
        pb={3} px={2} w="100%"
        fontFamily="Work sans"
        d="flex"
        justifyContent={{ base: "space-between" }}
        alignItems="center"
      >
          <IconButton
              d={{ base: "flex", md: "none" }}
              icon={<ArrowBackIcon />}
              onClick={() => setSelectedChat("")}
            />
          {!selectedChat.isGroupChat?
          (<> {getSender(user, selectedChat.users)}
          <ProfileModal
                    user={getSenderFull(user, selectedChat.users)}
                  /></>):(
            <>{selectedChat.chatName.toUpperCase()}
            <UpdateGroupChatModal
                    fetchAgain={fetchAgain}
                    setFetchAgain={setFetchAgain}
            />
            </>
          )}
            </Text>
            <Box
            d="flex" flexDir="column" justifyContent="flex-end"
            p={3} bg="#E8E8E8" w="100%"
            h="100%"
            borderRadius="lg"
            overflowY="hidden">
                {loading ? (
              <Spinner size="xl" w={20}
                h={20}
                alignSelf="center"
                margin="auto"
              />
            ) : (
              <div>
                {/* message */}
              </div>
            )}
            
            <FormControl onKeyDown={sendMessage} id="first-name" isRequired
              mt={3}
            >
              <Input
                variant="filled"
                bg="#E0E0E0"
                placeholder="Enter a message.."
                value={newMessage}
                onChange={typingHandler}
              />
            </FormControl>
            </Box>
      </>):
      (
        <Box d="flex" alignItems="center" justifyContent="center" h="100%">
          <Text fontSize="3xl" pb={3} fontFamily="Work sans">
            Click on a user to start chatting
          </Text>

        </Box>)}
    </>
  )
}

export default SingleChats
