import { FormControl, FormLabel, VStack,Input, InputGroup,InputRightElement,Button } from '@chakra-ui/react'
import React, { useState } from 'react'
import { useToast } from '@chakra-ui/react'
const Signup = () => {
    const [show,setShow] = useState(false);
    const [name,setName] = useState();
    const [email,setEmail] = useState();
    const [confirmpassword,setconfirmpassword] = useState();
    const [password,setPassword] = useState();
    const [pic,setPic] = useState();
    const [loading,setLoading] = useState(false);
    const toast = useToast()

    const handleClick = () => setShow(!show);

    const postDetails = (pics) => {
      setLoading(true);
      if(pic === undefined){
           toast({
             title: "Please select an Image!",
             status: "warning",
             duration:5000,
             isClosable: true,
             position:"bottom"
           })
           return ;
      }
      if(pics.type === "image/jpeg" || pics.type === "image/jpeg" ){
         const data = new FormData();
         data.append("file",pics);
         data.append("upload_preset","CHATAPP");
         data.append("cloud_name", "dz1gdhrfv");
         fetch("https://api.cloudinary.com/v1_1/dz1gdhrfv/image/upload",{
           method:"post",
           body: data,
         }).then((res) => res.json())
         .then(data =>{
           setPic(data.url.toString());
           setLoading(false);
         }).catch((err) =>{
           console.log(err);
           setLoading(false);
         });
      }else{
        toast({
          title: "Please select an Image!",
          status: "warning",
          duration:5000,
          isClosable: true,
          position:"bottom"
        })
        setLoading(false);
        return ;

      }
    };

    const submitHandler = () => {};


  return (
    <VStack spacing="5px" color="black">
      <FormControl isRequired id='FirstName'>
          <FormLabel>Name</FormLabel>
          <Input placeholder='Enter Your Name' 
          onChange={(e) => setName(e.target.value)}></Input>
      </FormControl>

      <FormControl isRequired id='FirstName'>
          <FormLabel>Email</FormLabel>
          <Input placeholder='Enter Your Email' 
          onChange={(e) => setEmail(e.target.value)}></Input>
      </FormControl>

      <FormControl isRequired id='FirstName'>
          <FormLabel>Password</FormLabel>
          <InputGroup>
          <Input type={show ? "text" : "password"} placeholder='Enter Password' 
          onChange={(e) => setPassword(e.target.value)}></Input>
          <InputRightElement width="4.5rem">
            <Button h="1.75rem" size="sm" onClick={handleClick}>
              {show ? "Hide" : "Show"}
            </Button>
            </InputRightElement>
          </InputGroup>
          
      </FormControl>
      <FormControl isRequired id='FirstName'>
          <FormLabel>Confirm Password</FormLabel>
          <InputGroup>
          <Input type={show ? "text" : "password"} placeholder='Confirm Password' 
          onChange={(e) => setconfirmpassword(e.target.value)}></Input>
          <InputRightElement width="4.5rem">
            <Button h="1.75rem" size="sm" onClick={handleClick}>
              {show ? "Hide" : "Show"}
            </Button>
            </InputRightElement>
          </InputGroup>
          
      </FormControl>

      <FormControl id="pic">
        <FormLabel>Upload your Picture</FormLabel>
        <Input
          type="file"
          p={1.5}
          accept="image/*"
          onChange={(e) => postDetails(e.target.files[0])}
        />
      </FormControl>

      <Button
        colorScheme="blue"
        width="100%"
        style={{ marginTop: 15 }}
        onClick={submitHandler}
        isLoading = {loading}
      >
        Sign Up
      </Button>
    </VStack>
  )
}

export default Signup
