import React from 'react'
import { ChatState } from "../Context/ChatProvider";
import { Box } from "@chakra-ui/react";

const Chatbox = () => {
  
  const {selectedChat} = ChatState();
  return (
    <Box
    d={{ base: selectedChat ? "flex" : "none", md: "flex" }}
      alignItems="center"
      flexDir="column"
      p={3}>
      Single CHat
    </Box>
  )
}

export default Chatbox
