import { Avatar, Box, Button, Drawer, DrawerBody, DrawerContent, DrawerHeader, DrawerOverlay, Input, Menu, MenuButton, MenuDivider, MenuItem, MenuList, Text, Tooltip, useToast } from '@chakra-ui/react';
import React, { useState } from 'react'
import {BellIcon, ChevronDownIcon  } from '@chakra-ui/icons'
import { ChatState } from '../../Context/ChatProvider';
import ProfileModal from './ProfileModal';
import { useHistory } from 'react-router-dom';
import { useDisclosure } from "@chakra-ui/hooks";

const SideDrawer = () => {
  const [search, setSearch] = useState("");
  const [searchResult, setSearchResult] = useState([]);
  const [loading, setLoading] = useState(false);
  const [loadingChat, setLoadingChat] = useState(false);
  const { user  } = ChatState();
  const { isOpen, onOpen, onClose } = useDisclosure();
  const toast = useToast();
  const history = useHistory();
  const logoutHandler = () => {
    toast({
        title: `Bye ${user.name} Hope to see you soon`,
        status: "warning",
        duration: 5000,
        isClosable: true,
        position: "bottom",
      });
    localStorage.removeItem("userInfo");
    history.push("/");
  };
  
 

  const handleSearch = async () => {
    if (!search) {
      toast({
        title: "Please Enter something in search",
        status: "warning",
        duration: 5000,
        isClosable: true,
        position: "top-left",
      });
      return;
    }}

  return (
    <div>
     <Box
     d="flex"
     justifyContent="space-between"
     alignItems="center"
     bg="white"
     w="100%"
     p="5px 10px 5px 10px"
     borderWidth="5px">
     <Tooltip label="Search Users to chat" hasArrow placement="bottom-end">
          <Button variant="ghost" onClick={onOpen}>
            <i className="fas fa-search"></i>
            <Text d={{ base: "none", md: "flex" }} px={4}>
              Search User
            </Text>
          </Button>
        </Tooltip>
        <Text fontSize="2xl" fontFamily="Work sans">
          Talk-A-Tive
        </Text>
        <div>
            <Menu>
            <MenuButton p={1}>
              <BellIcon fontSize="2xl" m={1} />
            </MenuButton>
            </Menu>
        
        <Menu>
            <MenuButton as={Button} bg="white" rightIcon={<ChevronDownIcon />}>
              <Avatar
                size="sm"
                cursor="pointer"
                name={user.name}
                src={user.pic}
              />
            </MenuButton>
            <MenuList>
            {/* <ProfileModal user={user}> */}
                <MenuItem>My Profile</MenuItem>{" "}
              {/* </ProfileModal> */}
             <MenuDivider />
              <MenuItem 
              onClick={logoutHandler}
              >Logout</MenuItem>
            </MenuList>
            </Menu>
            </div>
     </Box>

     <Drawer placement="left" onClose={onClose} isOpen={isOpen}>
         <DrawerOverlay></DrawerOverlay>
         <DrawerContent>
         <DrawerHeader borderBottomWidth="1px">Search Users</DrawerHeader>
         <DrawerBody>
            <Box d="flex" pb={2}>
              <Input
                placeholder="Search by name or email"
                mr={2}
                value={search}
                onChange={(e) => setSearch(e.target.value)}
              />
              <Button 
              onClick={handleSearch}
              >Search</Button>
            </Box>
            </DrawerBody>
         </DrawerContent>
     </Drawer>
     
    </div>
  )
}

export default SideDrawer;
